import './AuthStyle.css'
import {Text, StyleSheet} from 'react-native';
import {useState} from 'react';
import { useNavigate } from 'react-router-dom';




function AuthForm() {
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]:value}));
    }
    const [inputs, setInputs] = useState({})
    const [, setError] = useState('');
    const navigate = useNavigate();
    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('/cityjail', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    ...inputs,
                    action: event.nativeEvent.submitter.name,
                }),
            });
            const data = await response.json();

            if (response.ok) {
                navigate('/cityjail/home');
            } else {
                setError(data.message || 'Attempt failed');
            }
        } catch (error) {
            setError('Unexpected error occured');
            //console.error(error);
        }
    };

    return (
        <>
        <div className="title">
                <Text style={styles.titleText}>City Jail Information Center</Text>
        </div>
        <form onSubmit={handleSubmit}>
            <div className="username">
                <Text style={styles.authText}>User Name:</Text>
            </div>
            <input type="text" name ="username" className="username-text" onChange={handleChange}/>

            <div className="password">
                <Text style={styles.authText}>Password:</Text>
            </div>
            <input type="text" name ="password" className="password-text" onChange={handleChange}/>

            <button type="submit" name="signIn" className="sign-in">
                <Text style={styles.buttonText}>Sign In</Text>
            </button>

            <button type="submit" name="createAccount" className="create-account">
                <Text style={styles.buttonText}>Create Account</Text>
            </button>
        </form>
        </>
    );
}

const styles = StyleSheet.create({
    titleText: {
      fontSize: 70,
      fontWeight: 'bold',
      color: 'white',
    },
    authText: {
        fontSize: 35,
        fontWeight: 'normal',
        color: 'black',
        top: 30,
    },
    buttonText: {
        fontSize: 24,
        fontWeight: 'normal',
        color: 'white',
    }
  });



export default AuthForm;
