import './AuthStyle.css'
import {Text, StyleSheet} from 'react-native';
import {useState} from 'react';
import axios from "axios";

function AuthForm() {
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]:value}));
    }
    const [inputs, setInputs] = useState({})
    const handleSubmit = (event) => {
        event.preventDefault();

        axios.post('http://localhost:3306/api/user/create', inputs);
        console.log(inputs);
    }
    return (
        <>
        <div className="title">
                <Text style={styles.titleText}>City Jail Information Center</Text>
        </div>
        <form onSubmit={handleSubmit}>
            <div className="username">
                <Text style={styles.authText}>User Name:</Text>
            </div>
            <input type="text" name ="username" className="username-text" onChange={handleChange}/>

            <div className="password">
                <Text style={styles.authText}>Password:</Text>
            </div>
            <input type="text" name ="password" class="password-text" onChange={handleChange}/>

            <button className="sign-in">
                <Text style={styles.buttonText}>Sign In</Text>
            </button>

            <button className="create-account">
                <Text style={styles.buttonText}>Create Account</Text>
            </button>
        </form>
        </>
    );
}

const styles = StyleSheet.create({
    titleText: {
      fontSize: 70,
      fontWeight: 'bold',
      color: 'white',
    },
    authText: {
        fontSize: 35,
        fontWeight: 'normal',
        color: 'black',
        top: 30,
    },
    buttonText: {
        fontSize: 24,
        fontWeight: 'normal',
        color: 'white',
    }
  });



export default AuthForm;
