import NavBar from "./NavBar";
import SignOut from "./SignOut";
import './Style.css';
import {useState, useEffect} from "react";
import axios from "axios";

function Criminals() {
    const [data, setData] = useState([]);
    const [selectedRowKeys, setSelectedRowKeys] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:3300/api/criminal-data", data)
        .then((res) => setData(res.data))
        .catch(err => { console.error(err); });
    }, []);

    const onSelectChange = (selectedKeys) => {
        setSelectedRowKeys(selectedKeys);
      };

      const rowSelection = {
        selectedRowKeys,
        onChange: onSelectChange,
      };


    return (
        <>
        <NavBar />
        <table className="criminals-table">
            <thead>
                <tr>
                    <th>Criminal ID</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Phone</th>
                </tr>
            </thead>

            <tbody>
                {
                    data.map((criminal, index)=>{
                        return <tr key={index}>
                            <td>
                                <input
                                    type="checkbox"
                    checked={selectedRowKeys.includes(index)}
                    onChange={() => onSelectChange([...selectedRowKeys, index])}
                  />
                </td>
                            <td>{criminal.Criminal_ID}</td>
                            <td>{criminal.Name}</td>
                            <td>{criminal.Address}</td>
                            <td>{criminal.Phone}</td>
                        </tr>
                    })
                }
            </tbody>
        </table>
        <SignOut />
        </>
    )
}

export default Criminals;
