import './AuthStyle.css'
import {Text, StyleSheet} from 'react-native';

function AuthForm() {

    const handleSubmit = (event) => {
        event.preventDefault();
    }
    return (
        <>
        <div className="title">
                <Text style={styles.titleText}>City Jail Information Center</Text>
        </div>
        <form onSubmit={handleSubmit}>
            <div className="username">
                <Text style={styles.authText}>User Name:</Text>
            </div>
            <input type="text" class="username-text"/>
            <div className="password">
                <Text style={styles.authText}>Password:</Text>
            </div>
            <input type="text" class="password-text"/>

            <button className="sign-in">
                <Text style={styles.buttonText}>Sign In</Text>
            </button>

            <button className="create-account">
                <Text style={styles.buttonText}>Create Account</Text>
            </button>
        </form>
        </>
    );
}

const styles = StyleSheet.create({
    titleText: {
      fontSize: 70,
      fontWeight: 'bold',
      color: 'white',
    },
    authText: {
        fontSize: 35,
        fontWeight: 'normal',
        color: 'black',
        top: 30,
    },
    buttonText: {
        fontSize: 24,
        fontWeight: 'normal',
        color: 'white',
    }
  });



export default AuthForm;
