import './AuthStyle.css'
import {Text, StyleSheet} from 'react-native';
import {useState} from 'react';
import { useNavigate } from 'react-router-dom';

const [users, setUsers] = useState([])
useEffect(()=>{
    const fetchallUsers = async ()=>{
        try {
            const res = await axios.get("http://localhost:3300/api/signin")
            console.log(res)
        } catch (err) {
            console.log(err)
        }
    }
    fetchallUsers()
},[])


function AuthForm() {
        return(
        <>
        <div className="title">
                <Text style={styles.titleText}>City Jail Information Center</Text>
        </div>
        <form>
            <div className="username">
                <Text style={styles.authText}>User Name:</Text>
            </div>
            <input type="text" name ="username" className="username-text"/>

            <div className="password">
                <Text style={styles.authText}>Password:</Text>
            </div>
            <input type="text" name ="password" className="password-text"/>

            <button type="submit" name="signIn" className="sign-in">
                <Text style={styles.buttonText}>Sign In</Text>
            </button>

            <button type="submit" name="createAccount" className="create-account">
                <Text style={styles.buttonText}>Create Account</Text>
            </button>
        </form>
        </>
    );
}

const styles = StyleSheet.create({
    titleText: {
      fontSize: 70,
      fontWeight: 'bold',
      color: 'white',
    },
    authText: {
        fontSize: 35,
        fontWeight: 'normal',
        color: 'black',
        top: 30,
    },
    buttonText: {
        fontSize: 24,
        fontWeight: 'normal',
        color: 'white',
    }
  });



export default AuthForm;
