import './Style.css'
import {Text, StyleSheet} from 'react-native';
import NavBar from './NavBar'
import { useNavigate } from 'react-router-dom';
import SignOut from './SignOut'
import React, { useState } from 'react';

function CriminalInfo() {
  const [criminalId, setCriminalId] = useState('');
  const [criminalData, setCriminalData] = useState(null);
  const [newCriminalData, setNewCriminalData] = useState({
    // Initialize with default values or empty strings
    name: '',
    age: '',
    // ... other fields
  });

  const handleCriminalIdChange = (event) => {
    setCriminalId(event.target.value);
  };

  const handleNewCriminalChange = (event) => {
    setNewCriminalData({
      ...newCriminalData,
      [event.target.name]: event.target.value,
    });
  };

  const handleViewCriminal = async () => {
    try {
      // Call your backend API to get criminal data by ID
      const data = await getCriminal(criminalId);
      setCriminalData(data);
    } catch (error) {
      console.error('Error fetching criminal data:', error);
    }
  };

  const handleAddCriminal = async () => {
    try {
      // Call your backend API to add a new criminal
      await addCriminal(newCriminalData);
      // Optionally, clear the form or reset newCriminalData
    } catch (error) {
      console.error('Error adding new criminal:', error);
    }
  };

  const handleEditCriminal = async () => {
    try {
      // Call your backend API to edit criminal data
      await editCriminal(criminalId, newCriminalData);
      // Optionally, clear the form or reset newCriminalData
    } catch (error) {
      console.error('Error editing criminal data:', error);
    }
  };

  const handleDeleteCriminal = async () => {
    try {
      // Call your backend API to delete a criminal by ID
      await deleteCriminal(criminalId);
      // Optionally, clear the form or reset newCriminalData
    } catch (error) {
      console.error('Error deleting criminal:', error);
    }
  };

  return (
    <div>
      <h2>Criminal Information</h2>
      <label>
        Criminal ID:
        <input type="text" value={criminalId} onChange={handleCriminalIdChange} />
      </label>
      <button onClick={handleViewCriminal}>View Criminal</button>

      {criminalData && (
        <div>
          {/* Display criminal information */}
          <h3>Criminal Details</h3>
          <pre>{JSON.stringify(criminalData, null, 2)}</pre>

          {/* Edit criminal information */}
          <h3>Edit Criminal</h3>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={newCriminalData.name}
              onChange={handleNewCriminalChange}
            />
          </label>
          {/* Add more input fields for other criminal information */}
          <button onClick={handleEditCriminal}>Edit Criminal</button>
        </div>
      )}

      {/* Add new criminal */}
      <h3>Add New Criminal</h3>
      <label>
        Name:
        <input type="text" name="name" value={newCriminalData.name} onChange={handleNewCriminalChange} />
      </label>
      {/* Add more input fields for other criminal information */}
      <button onClick={handleAddCriminal}>Add Criminal</button>

      {/* Delete criminal */}
      <h3>Delete Criminal</h3>
      <button onClick={handleDeleteCriminal}>Delete Criminal</button>
    </div>
  );
}

export default CriminalInfo;
